import { useState, useEffect } from "react";
import { submitGuestParking, getParkingAvailability } from "../api/client";
import Modal from "react-modal";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

Modal.setAppElement("#root");

export default function ParkingForm({ invitationId }) {
  const [availableSpots, setAvailableSpots] = useState("");
  const [note, setNote] = useState("");
  const [weddingPlace, setWeddingPlace] = useState("");
  const [weddingTime, setWeddingTime] = useState("");
  const [busy, setBusy] = useState(false);
  const [parkingLeft, setParkingLeft] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  // Fetch real-time parking availability
  useEffect(() => {
    async function fetchAvailability() {
      try {
        const available = await getParkingAvailability();
        setParkingLeft(available);
      } catch (err) {
        console.error("Failed to fetch availability");
      }
    }
    fetchAvailability();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      setBusy(true);
      await submitGuestParking(invitationId, {
        availableSpots: availableSpots === "" ? null : Number(availableSpots),
        note: note.trim() || null,
        weddingPlace: weddingPlace.trim() || null,
        weddingTime: weddingTime.trim() || null,
      });

      toast.success("Thanks! Your parking info has been recorded.");
      setModalOpen(true);

      // Reset form
      setAvailableSpots("");
      setNote("");
      setWeddingPlace("");
      setWeddingTime("");

      // Refresh availability
      const updated = await getParkingAvailability();
      setParkingLeft(updated);
    } catch (err) {
      toast.error(err?.response?.data?.message || "Submission failed. Try again.");
    } finally {
      setBusy(false);
    }
  };

  const style = `
    .parking-form-wrapper {
      max-width: 620px;
      margin: 24px auto 0;
      padding: 0 16px 40px;
      font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
    }

    .parking-card {
      background: #ffffff;
      border-radius: 20px;
      padding: 26px 24px 24px;
      box-shadow: 0 12px 30px rgba(0, 0, 0, 0.08);
      animation: parkingFadeIn 0.35s ease;
    }

    .parking-card-title {
      font-size: 20px;
      font-weight: 700;
      color: #b03386;
      margin-bottom: 10px;
      text-align: left;
    }

    .parking-status-text {
      font-size: 13px;
      color: #7a4ba1;
      margin-bottom: 18px;
    }

    .parking-status-text span {
      font-weight: 600;
    }

    .parking-field {
      display: flex;
      flex-direction: column;
      gap: 6px;
      margin-bottom: 14px;
    }

    .parking-label {
      font-size: 14px;
      font-weight: 500;
      color: #5a4c73;
    }

    .parking-input,
    .parking-textarea {
      width: 100%;
      padding: 9px 11px;
      border-radius: 12px;
      border: 1px solid #e0d5f4;
      background: #faf8ff;
      font-size: 14px;
      transition: border-color 0.2s ease, box-shadow 0.2s ease, background 0.2s ease;
    }

    .parking-input:focus,
    .parking-textarea:focus {
      border-color: #c0abea;
      background: #f4f0ff;
      box-shadow: 0 0 0 2px rgba(192, 171, 234, 0.25);
      outline: none;
    }

    .parking-textarea {
      min-height: 72px;
      resize: vertical;
    }

    .parking-row-two {
      display: flex;
      flex-direction: column;
      gap: 14px;
    }

    @media (min-width: 640px) {
      .parking-row-two {
        flex-direction: row;
      }
      .parking-row-two .parking-field {
        flex: 1;
      }
    }

    .parking-submit-btn {
      margin-top: 6px;
      width: 100%;
      padding: 11px 0;
      border-radius: 14px;
      border: none;
      background: linear-gradient(90deg, #b872d9, #a246c7);
      color: #fff;
      font-size: 15px;
      font-weight: 600;
      cursor: pointer;
      transition: opacity 0.25s ease, transform 0.25s ease;
    }

    .parking-submit-btn:hover:not(:disabled) {
      opacity: 0.9;
      transform: translateY(-1px);
    }

    .parking-submit-btn:disabled {
      opacity: 0.6;
      cursor: default;
    }

    /* Modal styles */
    .parking-modal {
      background: #ffffff;
      border-radius: 18px;
      padding: 24px 24px 18px;
      max-width: 360px;
      margin: auto;
      box-shadow: 0 16px 40px rgba(0, 0, 0, 0.18);
      outline: none;
      text-align: center;
      font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
    }

    .parking-modal h2 {
      margin: 0 0 8px;
      font-size: 20px;
      color: #b03386;
    }

    .parking-modal p {
      font-size: 14px;
      color: #555;
      margin-bottom: 18px;
    }

    .parking-modal button {
      padding: 9px 18px;
      border-radius: 999px;
      border: none;
      background: linear-gradient(90deg, #b872d9, #a246c7);
      color: #fff;
      font-size: 14px;
      font-weight: 600;
      cursor: pointer;
    }

    .parking-overlay {
      background: rgba(0, 0, 0, 0.35);
      position: fixed;
      inset: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      z-index: 9999;
    }

    @keyframes parkingFadeIn {
      from { opacity: 0; transform: translateY(10px); }
      to { opacity: 1; transform: translateY(0); }
    }
  `;

  return (
    <>
      <style>{style}</style>

      <div className="parking-form-wrapper">
        <form className="parking-card" onSubmit={onSubmit}>
          <h2 className="parking-card-title">Inform Parking Capacity</h2>

          {parkingLeft !== null && (
            <p className="parking-status-text">
              üÖøÔ∏è Available parking spots: <span>{parkingLeft}</span>
            </p>
          )}

          <div className="parking-field">
            <label className="parking-label">Wedding Place Name</label>
            <input
              className="parking-input"
              type="text"
              placeholder="e.g., Grand Palace Hotel, Garden Venue‚Ä¶"
              value={weddingPlace}
              onChange={(e) => setWeddingPlace(e.target.value)}
            />
          </div>

          <div className="parking-row-two">
            <div className="parking-field">
              <label className="parking-label">Wedding Date & Time</label>
              <input
                className="parking-input"
                type="datetime-local"
                value={weddingTime}
                onChange={(e) => setWeddingTime(e.target.value)}
              />
            </div>

            <div className="parking-field">
              <label className="parking-label">Number of Cars</label>
              <input
                className="parking-input"
                type="number"
                min="0"
                placeholder="e.g., 4"
                value={availableSpots}
                onChange={(e) => setAvailableSpots(e.target.value)}
              />
            </div>
          </div>

          <div className="parking-field">
            <label className="parking-label">Note</label>
            <textarea
              className="parking-textarea"
              rows="3"
              placeholder="Describe location, timing, or constraints‚Ä¶"
              value={note}
              onChange={(e) => setNote(e.target.value)}
            />
          </div>

          <button
            type="submit"
            disabled={busy}
            className="parking-submit-btn"
          >
            {busy ? "Sending‚Ä¶" : "Submit"}
          </button>
        </form>

        {/* Toast notifications */}
        <ToastContainer position="top-right" autoClose={3000} />

        {/* Confirmation modal */}
        <Modal
          isOpen={modalOpen}
          onRequestClose={() => setModalOpen(false)}
          contentLabel="Confirmation"
          className="parking-modal"
          overlayClassName="parking-overlay"
        >
          <h2>üéâ Submission Successful</h2>
          <p>Your parking details have been saved. Thank you!</p>
          <button onClick={() => setModalOpen(false)}>Close</button>
        </Modal>
      </div>
    </>
  );
}
