import { useParams } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function AddPhotos() {
  const { invitationId } = useParams();
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a photo.");
      return;
    }

    const formData = new FormData();
    formData.append("photo", selectedFile);

    try {
      await axios.post(
        `http://localhost:5000/api/guests/events/${invitationId}/photos`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      alert("Photo uploaded successfully!");
    } catch (err) {
      console.error(err);
      alert("Upload failed.");
    }
  };

  // CLEAN & ELEGANT NEW STYLE (no dashed, no purple shapes)
  const elegantStyle = `
    body {
      background: #fcf7fa;
      font-family: 'Inter', sans-serif;
    }

    .upload-wrapper {
      display: flex;
      justify-content: center;
      padding-top: 40px;
    }

    .upload-card {
      background: white;
      width: 450px;
      padding: 40px;
      border-radius: 25px;
      text-align: center;
      box-shadow: 0 10px 32px rgba(0,0,0,0.08);
      animation: fadeIn 0.4s ease;
    }

    .upload-title {
      font-size: 30px;
      font-weight: 700;
      color: #a12479;
      margin-bottom: 8px;
    }

    .upload-subtitle {
      font-size: 19px;
      color: #7b4ba1;
      margin-bottom: 20px;
    }

    .upload-desc {
      font-size: 15px;
      color: #666;
      margin-bottom: 30px;
    }

    /* NEW CLEAN INPUT BOX */
    .upload-input-box {
      width: 100%;
      background: #faf2fb;
      padding: 15px;
      border-radius: 10px;
      border: 2px solid #e4c9ea;
      cursor: pointer;
      transition: 0.2s;
      margin-bottom: 25px;
    }

    .upload-input-box:hover {
      background: #f3e3f5;
      border-color: #d1a3da;
    }

    .upload-input {
      display: none;
    }

    .upload-label {
      font-size: 16px;
      font-weight: 600;
      color: #8a479b;
    }

    .upload-btn {
      width: 100%;
      padding: 14px;
      background: linear-gradient(90deg, #b872d9, #a246c7);
      color: white;
      border: none;
      border-radius: 12px;
      font-size: 17px;
      font-weight: 600;
      cursor: pointer;
      transition: 0.25s;
    }

    .upload-btn:hover {
      opacity: 0.88;
      transform: translateY(-1px);
    }

    @keyframes fadeIn {
      from { opacity: 0; transform: translateY(10px); }
      to { opacity: 1; transform: translateY(0); }
    }
  `;

  return (
    <>
      <style>{elegantStyle}</style>

      <div className="upload-wrapper">
        <div className="upload-card">
          <h1 className="upload-title">Guest â€” Add Photos</h1>
          <h2 className="upload-subtitle">Share Wedding Photos</h2>

          <p className="upload-desc">
            Upload your favorite moments for the couple!
          </p>

          {/* CLEAN SELECT FILE BOX */}
          <label className="upload-input-box">
            <span className="upload-label">
              {selectedFile ? selectedFile.name : "Click to choose a photo"}
            </span>
            <input
              type="file"
              accept="image/*"
              className="upload-input"
              onChange={handleFileChange}
            />
          </label>

          <button className="upload-btn" onClick={handleUpload}>
            Upload Photo
          </button>
        </div>
      </div>
    </>
  );
}
