// src/Guest/album.jsx
import React, { useState } from "react";
import "./album.css";
import axios from "axios";

export default function WeddingAlbum() {
  const [coupleName, setCoupleName] = useState("");
  const [photos, setPhotos] = useState([]);
  const [error, setError] = useState(null);

  const fetchPhotos = async () => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL;
      const response = await axios.get(
        `${apiUrl}/api/wedding/photos?couple_name=${coupleName}`
      );
      setPhotos(response.data);
      setError(null);
    } catch (err) {
      setError("Failed to fetch photos.");
      setPhotos([]);
    }
  };

  return (
    <div className="album-page">
      <div className="album-card">
        <h1 className="album-title">Guest â€” Wedding Album</h1>
        <h2 className="album-subtitle">Relive the Wedding Moments</h2>
        <p className="album-desc">
          Enter the couple&apos;s name to view all the photos shared for their
          special day. ðŸ’–
        </p>

        <input
          className="album-input"
          type="text"
          placeholder="Couple's name e.g. Emma & David"
          value={coupleName}
          onChange={(e) => setCoupleName(e.target.value)}
        />

        <button className="album-btn" onClick={fetchPhotos}>
          Show Album
        </button>

        {error && <p className="album-error">{error}</p>}

        {photos.length > 0 ? (
          <div className="album-gallery-wrapper">
            <h3 className="album-gallery-title">
              Album of <span>{coupleName}</span>
            </h3>
            <div className="album-gallery">
              {photos.map((photo) => (
                <div className="album-photo-card" key={photo.photo_url}>
                  <div className="album-photo-frame">
                    <img
                      src={`http://localhost:5000/${photo.photo_url}`}
                      alt="Wedding"
                    />
                  </div>
                  <p className="album-photo-meta">
                    Uploaded by <strong>{photo.uploaded_by || "Guest"}</strong>
                  </p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          !error && (
            <p className="album-empty">
              No photos found for this couple yet. Try another name or ask the
              guests to upload some memories. ðŸŒ¸
            </p>
          )
        )}
      </div>
    </div>
  );
}
