import { useParams } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function AddPhotos() {
  const { invitationId } = useParams();
  const [selectedFile, setSelectedFile] = useState(null);

  // Handle file select
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  // Upload photo
  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a photo.");
      return;
    }

    const formData = new FormData();
    formData.append("photo", selectedFile);

    try {
      await axios.post(
        `http://localhost:5000/api/guests/events/${invitationId}/photos`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      alert("Photo uploaded successfully!");
    } catch (err) {
      console.error(err);
      alert("Upload failed.");
    }
  };

  // ---------------------------
  // BEAUTIFUL ELEGANT CSS STYLE
  // ---------------------------
  const elegantStyle = `
    body {
      background: #fcf7fa;
      font-family: 'Inter', sans-serif;
    }

    .upload-wrapper {
      display: flex;
      justify-content: center;
      padding-top: 40px;
    }

    .upload-card {
      background: white;
      width: 420px;
      padding: 35px;
      border-radius: 22px;
      box-shadow: 0 8px 28px rgba(0, 0, 0, 0.08);
      text-align: center;
      animation: fadeIn 0.5s ease-in-out;
    }

    .upload-title {
      font-size: 28px;
      font-weight: 700;
      color: #a12479;
      margin-bottom: 5px;
    }

    .upload-subtitle {
      font-size: 18px;
      color: #7b4ba1;
      margin-bottom: 25px;
    }

    .upload-desc {
      color: #555;
      margin-bottom: 25px;
    }

    .photo-box {
      border: 2px dashed #d8b3dd;
      background: #faf2fb;
      padding: 25px;
      border-radius: 15px;
      cursor: pointer;
      transition: 0.3s;
      margin-bottom: 20px;
    }

    .photo-box:hover {
      border-color: #b77ac8;
      background: #f5e6f8;
    }

    .upload-input {
      display: none;
    }

    .upload-btn {
      width: 100%;
      padding: 12px 0;
      border: none;
      border-radius: 10px;
      background: #b872d9;
      color: white;
      font-size: 16px;
      font-weight: 600;
      cursor: pointer;
      transition: 0.3s;
    }

    .upload-btn:hover {
      background: #9b5aba;
    }

    @keyframes fadeIn {
      from { opacity: 0; transform: translateY(12px); }
      to { opacity: 1; transform: translateY(0); }
    }
  `;

  return (
    <>
      {/* Elegant inline CSS */}
      <style>{elegantStyle}</style>

      <div className="upload-wrapper">
        <div className="upload-card">

          <h1 className="upload-title">Guest — Add Photos</h1>
          <h2 className="upload-subtitle">Share Wedding Photos</h2>
          <p className="upload-desc">
            Upload your favorite moments for the couple!
          </p>

          {/* Upload zone */}
          <label className="photo-box">
            <div style={{ fontSize: "16px", fontWeight: "600", color: "#8a479b" }}>
              Click to choose a photo
            </div>
            <div style={{ fontSize: "13px", color: "#777" }}>
              JPG / PNG • max 10 MB
            </div>
            <input
              type="file"
              accept="image/*"
              className="upload-input"
              onChange={handleFileChange}
            />
          </label>

          <button className="upload-btn" onClick={handleUpload}>
            Upload Photo
          </button>

        </div>
      </div>
    </>
  );
}
