import { useParams } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function AddPhotos() {
  const { invitationId } = useParams();
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a photo.");
      return;
    }

    const formData = new FormData();
    formData.append("photo", selectedFile);

    try {
      await axios.post(
        `http://localhost:5000/api/guests/events/${invitationId}/photos`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      alert("Photo uploaded successfully!");
      setSelectedFile(null);
    } catch (err) {
      console.error(err);
      alert("Upload failed.");
    }
  };

  const pageStyle = `
    body {
      background: #fcf7fa;
      font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
    }

    .guest-upload-page {
      min-height: 100vh;
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 40px 16px;
    }

    .guest-upload-card {
      width: 100%;
      max-width: 520px;
      background: #ffffff;
      border-radius: 24px;
      box-shadow: 0 14px 35px rgba(0, 0, 0, 0.09);
      padding: 36px 32px 32px;
      text-align: center;
      animation: fadeIn 0.35s ease;
    }

    .guest-upload-title {
      font-size: 26px;
      font-weight: 700;
      color: #b03386;
      margin-bottom: 6px;
    }

    .guest-upload-subtitle {
      font-size: 18px;
      color: #7a4ba1;
      margin-bottom: 18px;
    }

    .guest-upload-desc {
      font-size: 15px;
      color: #555;
      margin-bottom: 26px;
    }

    .guest-upload-label {
      display: block;
      text-align: left;
      font-size: 14px;
      font-weight: 500;
      color: #5a4c73;
      margin-bottom: 6px;
    }

    .guest-upload-input {
      width: 100%;
      padding: 11px 12px;
      border-radius: 12px;
      border: 1px solid #e0d5f4;
      background: #faf8ff;
      font-size: 14px;
      cursor: pointer;
      transition: border-color 0.2s ease, box-shadow 0.2s ease, background 0.2s ease;
    }

    .guest-upload-input:hover,
    .guest-upload-input:focus {
      border-color: #c0abea;
      background: #f4f0ff;
      box-shadow: 0 0 0 2px rgba(192, 171, 234, 0.25);
      outline: none;
    }

    .guest-upload-file-name {
      margin-top: 8px;
      font-size: 13px;
      color: #777;
      text-align: left;
    }

    .guest-upload-btn {
      margin-top: 22px;
      width: 100%;
      padding: 13px 0;
      border-radius: 14px;
      border: none;
      background: linear-gradient(90deg, #b872d9, #a246c7);
      color: #fff;
      font-size: 16px;
      font-weight: 600;
      cursor: pointer;
      transition: opacity 0.25s ease, transform 0.25s ease;
    }

    .guest-upload-btn:hover {
      opacity: 0.9;
      transform: translateY(-1px);
    }

    @keyframes fadeIn {
      from { opacity: 0; transform: translateY(10px); }
      to { opacity: 1; transform: translateY(0); }
    }
  `;

  return (
    <>
      <style>{pageStyle}</style>

      <div className="guest-upload-page">
        <div className="guest-upload-card">
          <h1 className="guest-upload-title">Guest â€” Add Photos</h1>
          <h2 className="guest-upload-subtitle">Share Wedding Photos</h2>
          <p className="guest-upload-desc">
            Upload your favorite moments for the couple!
          </p>

          <label className="guest-upload-label">Photo file</label>
          <input
            type="file"
            accept="image/*"
            className="guest-upload-input"
            onChange={handleFileChange}
          />

          {selectedFile && (
            <div className="guest-upload-file-name">
              Selected: {selectedFile.name}
            </div>
          )}

          <button className="guest-upload-btn" onClick={handleUpload}>
            Upload Photo
          </button>
        </div>
      </div>
    </>
  );
}
